import { Component } from '@angular/core';
import {
  animate,
  state,
  style,
  transition,
  trigger,
} from '@angular/animations';

@Component({
  selector: 'app-enter',
  templateUrl: './enter.component.html',
  styleUrls: ['./enter.component.scss'],
  animations: [
    trigger('slide', [
      state(
        '*',
        style({
          opacity: 0,
          transform: 'translateX(-100px)',
        })
      ),
      state(
        'visible',
        style({
          opacity: 1,
        })
      ),
      transition(':enter', animate('400ms 300ms ease-out')),
      transition('* => visible', animate('200ms ease-out')),
      transition('visible => *', animate('400ms ease-out')),
    ]),
  ],
})
export class EnterComponent {
  show = false;

  get visibleState() {
    return this.show ? 'hidden' : 'visible';
  }

  toggleVisible() {
    this.show = !this.show;
  }

  logStart(event: any) {
    console.log('animation start', event);
  }

  logDone(event: any) {
    console.log('animation end', event);
  }
}
<section class="one">
  <h1 class="title right">State animation</h1>
  <div class="content">
    <code class="subtitle">@angular/animations</code>
    <p style="margin-top: 5px">Integrated animation builder</p>
    <code class="subtitle">@angular/animations/browser</code>
    <p style="margin-top: 5px">
      Provides infrastructure for cross-platform rendering of animations in a
      browser.
    </p>
    <code class="subtitle" style="display: flex; margin-top: 25px"
      >@angular/animations/browser/testing</code
    >
    <p style="margin-top: 5px">
      Provides infrastructure for testing of the Animations browser subsystem.
    </p>
  </div>

  <div class="card-wrapper">
    <button (click)="toggleVisible()">
      {{ show ? "Show" : "Hide" }} implementation
    </button>

    <div
      class="card"
      [@slide]="visibleState"
      (@slide.start)="logStart($event)"
      (@slide.done)="logDone($event)"
    >
      <p>
        To enable angular animations <code>BrowserAnimationsModule</code> need
        to be imported either in <code>app.module.ts</code> or in the related
        component where the animations should be applied.
      </p>
      <code class="import"
        >{{
          "import { BrowserAnimationsModule } from '@angular/platform-browser/animations';"
        }}}</code
      >
    </div>
  </div>
</section>
